.bulb1 {
  fill: rgba(255, 153, 0, 0.877);
  transform: scale(0.6);
}

.bulb1:hover {
  margin-top: 1px;
  transform: scale(0.85);
  transition-duration: 1.5s;
  fill: rgb(255, 217, 0);
}

/* early light bulbs animation */

.lightpic {
  fill: rgba(255, 153, 0, 0.877);
}

.lightpic:hover {
  fill: rgb(255, 217, 0);
  transform: rotate(180deg);
  transition-duration: 1.5s;
}

/* the incandescent light bulb animation */

.ideahead {
  animation-duration: 5s;
  animation-iteration-count: infinite;
  animation-name: headmove;
}

@keyframes headmove {
  0% {
    fill: rgba(255, 153, 0, 0.877);
    width: 50px;
  }

  100% {
    fill: rgb(255, 217, 0);
    width: 400px;
  }
}

/* tungsten filaments animation */

.bulb2 {
  transform: scale(0.75);
}

.bulb2 .innerlight {
  animation-duration: 1s;
  animation-iteration-count: infinite;
  animation-name: bulb2move;
  /*transform: scale(0.75);*/
  transition-duration: 1.5s;
}

@keyframes bulb2move {
  0% {
    fill: rgb(255, 217, 0);
  }

  25% {
    fill: rgba(255, 153, 0, 0.877);
  }

  50% {
    fill: orange;
  }

  75% {
    fill: orangered;
  }

  100% {
    fill: rgb(255, 217, 0);
  }
}

/* future of light bulbs animation */

.future-light .lightning {
  fill: rgba(255, 153, 0, 0.877);
  animation-name: lightningcolors;
  animation-duration: 1s;
  animation-iteration-count: infinite;
}

@keyframes lightningcolors {
  0% {
    fill: rgb(255, 217, 0);
  }

  25% {
    fill: rgba(255, 153, 0, 0.877);
  }

  50% {
    fill: orange;
  }

  75% {
    fill: orangered;
  }

  100% {
    fill: rgb(255, 217, 0);
  }
}

@media only screen and (min-width: 768px) {
  .gridtext {
    width: 400px;
    margin: 0 auto;
  }

  p {
    padding: 20px 0px 0px;
  }
}

